import React, { useState, useEffect } from 'react';
import './vet_history.css';

const vets = [
  { id: 1, name: '개진상', gender:'여', age: 45, experience: '20년', hospital: '삼성동물병원', position: '원장', 
    photo: 'data:image/jpeg;base64,' },
  { id: 2, name: '진상개', gender:'여', age: 38, experience: '15년', hospital: '연세동물병원',  position: '부원장', 
    photo: 'data:image/jpeg;base64,' },
  { id: 3, name: '상개진', gender:'남', age: 50, experience: '7년', hospital: '서울동물병원', position: '직원', 
    photo: 'data:image/jpeg;base64,' },
  { id: 4, name: '수인턴', gender:'남', age: 30, experience: '3년', hospital: '행복동물병원', position: '인턴', 
    photo: 'data:image/jpeg;base64,'} ,
];

function Vet_history() {
  const [history, setHistory] = useState([]);
  const [selectedConsultation, setSelectedConsultation] = useState(null); // 선택된 상담 내용
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 창 상태

  useEffect(() => {
    // 로컬 스토리지에서 모든 상담 기록을 불러옴
    const allHistory = JSON.parse(localStorage.getItem('vet_history')) || []; 
    setHistory(allHistory.reverse());
  }, []);

  const openModal = (consultation) => {
    setSelectedConsultation(consultation);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedConsultation(null);
  };

  return (
    <div className="history-container">
      <h2>상담 내역</h2>
      {history.map(({ vet, messages }) => (
        messages.length > 0 && (
          <div
            key={vet.id}
            className="consultation-summary"
            onClick={() => openModal({ vet, messages })}
          >
            <h3>{vet.name} 수의사</h3>
            <p>Date: {new Date(messages[0].date).toLocaleDateString()}</p>
          </div>
        )
      ))}

      {isModalOpen && selectedConsultation && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>{selectedConsultation.vet.name} 수의사와의 상담 내용</h3>
            <ul className="chat-history">
              {selectedConsultation.messages.map((msg, index) => (
                <li
                  key={index}
                  className={`chat-message ${msg.sender === '나' ? 'user-message' : 'vet-message'}`}
                >
                  <span>{msg.text}</span>
                  {msg.image && <img src={msg.image} alt="Sent" className="sent-image" />}
                </li>
              ))}
            </ul>
            <button onClick={closeModal} className="close-button">닫기</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Vet_history;
